import React from 'react';
import PropTypes from 'prop-types';
import Loading from './Loading';
import { addSong } from '../services/favoriteSongsAPI';

class AlbumMusics extends React.Component {
  constructor() {
    super();

    this.state = {
      favorites: [],
      loadingPage: false,
    };
  }

  componentDidMount() {
  }

  isFavorite = (trackId) => {
    const { favorites } = this.state;
    favorites.includes(trackId) ? true : false

    return false
  }

  handleCheckbox = (id) => {
    this.setState({
      loadingPage: true,
    }, async () => {
      const { musics } = this.props;
      const musicFinded = musics
        .find(({ trackId }) => id === trackId);
      await addSong(musicFinded);
      this.setState((prevState) => ({
        favorites: [...prevState.favorites, musicFinded],
      }));
      this.setState({
        loadingPage: false,
      });
      console.log('favoritada');
    });
  };

  render() {
    const { musics } = this.props;
    const {
      loadingPage,
    } = this.state;

    return (
      <section>
        { loadingPage ? <Loading /> : (
          <div>
            { musics
              .filter((music) => music !== musics[0])
              .map((
                {
                  trackName,
                  previewUrl,
                  trackId,
                },
              ) => (
                <div
                  key={ trackId }
                >
                  <div>{trackName}</div>

                  <audio
                    data-testid="audio-component"
                    src={ previewUrl }
                    controls
                  >
                    <track kind="captions" />
                    O seu navegador n√£o suporta o elemento
                    {' '}
                    {' '}
                    <code>audio</code>
                    .
                  </audio>

                  <label htmlFor={ trackId }>
                    {' '}
                    Favorita
                    <input
                      id={ trackId }
                      type="checkbox"
                      data-testid={ `checkbox-music-${trackId}` }
                      onClick={ () => this.handleCheckbox(trackId) }
                      // checked={ ()=> this.isFavorite(trackId) }
                    />
                  </label>
                </div>))}
          </div>
        )}
      </section>
    );
  }
}

AlbumMusics.propTypes = {
  musics: PropTypes.shape({
    map: PropTypes.func.isRequired,
    filter: PropTypes.func.isRequired,
    length: PropTypes.number.isRequired,
    collectionId: PropTypes.string.isRequired,
    trackName: PropTypes.string.isRequired,
    previewUrl: PropTypes.string.isRequired,
  }).isRequired,
};

export default AlbumMusics;
