import React from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';

class Search extends React.Component {
  constructor() {
    super();

    this.state = {
      searchInput: '',
      buttonIsDisabled: true,
    };
  }

  verifyChararcters = ({ target }) => {
    const { value } = target;
    const minNumber = 2;
    const minimumCharactersReached = value.length < minNumber;

    this.setState({
      searchInput: value,
      buttonIsDisabled: minimumCharactersReached,
    });
  }

  clickButton = async (e) => {
    const { searchInput } = this.state;
    e.preventDefault();

    const top = await searchAlbumsAPI(searchInput);

    this.setState({
      searchInput: '',
    });
  };

  render() {
    const { buttonIsDisabled, searchInput } = this.state;
    return (
      <div data-testid="page-search">
        <h1>Search</h1>
        <Header />

        <form>
          <label
            htmlFor="search-input"
          >
            <input
              id="search-input"
              data-testid="search-artist-input"
              onChange={ this.verifyChararcters }
              value={ searchInput }
            />
          </label>

          <button
            type="submit"
            data-testid="search-artist-button"
            disabled={ buttonIsDisabled }
            onClick={ this.clickButton }
          >
            Pesquisar
          </button>
        </form>
      </div>
    );
  }
}

export default Search;
