class ArtistAlbuns extends React.Component {
  render() {
    const { artist, albuns } = this.props;
    return (
      <section>
        { albuns.length > 0
          ?
            <><p>
            Resultados de:
            {' '}
            {artist}
          </p><div>
              {albuns
                .map((album) => (
                  <div key={album.collectionId}>
                    <img src={album.artworkUrl100} alt={album.collectionName} />

                    <div>{album.collectionName}</div>

                    <div>{album.artistName}</div>
                  </div>))}
            </div></>

          : <p>Nenhum Ã¡lbum foi encontrado</p>}
      </section>
    );
  }
}

